// https://github.com/medipass/react-credit-card-input/blob/master/src/utils/formatter.js
const DEFAULT_CVC_LENGTH = 3
const DEFAULT_CARD_MAX = 16
const DEFAULT_CARD_MIN = 13
export const DEFAULT_CARD_FORMAT = /(\d{1,4})/g
const DEFAULT_CARD_SVG_LOGO =
  'data:image/svg+xml;base64,'
const DEFAULT_SECURITY_CODE_NAME = 'CVC'

type CardType = {
  cvcLength: number
  format: RegExp
  logo: string
  luhn?: boolean
  maxCardNumberLength: number
  minCardNumberLength: number
  securityCodeName: string
  startPattern: RegExp
  type: CardNameType
}
const CARD_DEFAULTS = {
  cvcLength: DEFAULT_CVC_LENGTH,
  format: DEFAULT_CARD_FORMAT,
  logo: DEFAULT_CARD_SVG_LOGO,
  maxCardNumberLength: DEFAULT_CARD_MAX,
  minCardNumberLength: DEFAULT_CARD_MIN,
  securityCodeName: DEFAULT_SECURITY_CODE_NAME
}

